import React, { Component } from "react";
import { connect } from "react-redux";
// import "../style/signup-form.css";
function mapStateToProps(state) {
  return {};
}
//createUser?nom=ghiles&prenom=yacine&login=ghiles@gmail.com&password=25139&sex=F&birth_date=02/07/1999
class SignUpForm extends Component {
  constructor(props) {
    super(props);
    this.state = { email:"" };
  }
  handleSubmit(event) {
    // this.props.login(this.state.username, this.state.password);
    event.preventDefault();
  }

  handleChangeName(e) {
    this.setState({ name: e.target.value });
  }

  handleChangeLName(e) {
    this.setState({ lastname: e.target.value });
  }

  handleChangeEmail(e) {
    this.setState({ email: e.target.value });
  }

  handleChangePassword(e) {
    this.setState({ password: e.target.value });
  }

  handleChangeCPassword(e) {
    // this.setState({ password: e.target.value });
  }

  handleChangeRememberMe(e) {
    this.setState({ rememberme: !this.state.rememberme });
    console.log(this.state.rememberme);
  }

  render() {
    return (
      <div>
        <div className="container-fluid">
          <div>
            <div className="container-fluid">
              <div className="row main-content bg-success text-center">
                <div className="col-md-4 text-center company__info">
                  <div className="company__logo">
                    <img
                      className="rounded mx-auto d-block logo"
                      src="https://logos.mybrandnewlogo.com/logos/Az2kxWD0XwI4W8pnBDvk/images/logo-Pwkxq5mKp1izl52XkYL9SD2p2x40.png"
                      alt="logo"
                      width="300"
                      height="400"
                    />
                  </div>
                </div>
                <div className="col-md-8 col-xs-12 col-sm-12 login_form ">
                  <div className="container-fluid">
                    <div className="row">
                      <h2>SignUP</h2>
                    </div>
                    <div className="row">
                      <form
                        onSubmit={this.handleSubmit.bind(this)}
                        method="POST"
                        className="form-group"
                      >
                        <div className="row">
                          <input
                            required
                            type="text"
                            name="first_name"
                            id="first_name"
                            className="form__input"
                            placeholder="First Name"
                            onChange={(e) => this.handleChangeName(e)}
                          />
                        </div>
                        <div className="row">
                          <input
                            required
                            type="text"
                            name="last_name"
                            id="last_name"
                            className="form__input"
                            placeholder="Last Name"
                            onChange={(e) => this.handleChangeLastName(e)}
                          />
                        </div>
                        <div className="row">
                          <input
                            onChange={(e) => this.handleChangeEmail(e)}
                            required
                            type="email"
                            name="login"
                            id="login"
                            className="form__input"
                            placeholder="Email"
                          />
                        </div>
                        <div className="row">
                          <input
                            onChange={(e) => this.handleChangePassword(e)}
                            required
                            type="password"
                            name="password"
                            id="password"
                            className="form__input"
                            placeholder="Password"
                          />
                        </div>
                        <div className="row">
                          <input
                            onChange={(e) => this.handleChangeCPassword(e)}
                            required
                            type="ConfirmPassword"
                            name="ConfirmPassword"
                            id="ConfirmPassword"
                            className="form__input"
                            placeholder="Confirm Password"
                          />
                        </div>
                        <div className="row">
                          <input type="submit" value="Submit" className="btn" />
                        </div>
                      </form>
                    </div>
                    <div className="row have_account">
                      <p>
                        You have an account? <a href="#">Login Here</a>
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps)(SignUpForm);
