import React, { Component } from "react";
import { connect } from "react-redux";

import NavBar from "./nav-bar";
import UserProfile from "../components/user-profile";
import UserPostList from "./user-post-list";

function mapStateToProps(state) {
  return {
    user: state.routing.locationBeforeTransitions.state.user,
    userCommentsList: state.routing.locationBeforeTransitions.state.userCommentsList,
    userfriendsCommentsList: state.routing.locationBeforeTransitions.state.userfriendsCommentsList,
    userFriendList:[]
  };
}

class UserHome extends Component {
  render() {
    const { nom, prenom, Sex, DateNaiss } = this.props.user;
    console.log(this.props.userComments.length)
    return (
      <div className="container-fluid">
        <NavBar />
        <div className="row">
          <div className="col-md">
            <UserProfile
              nom={nom}
              prenom={prenom}
              Sex={Sex}
              DateNaiss={DateNaiss}
              numberOfcomments = {this.props.userComments.length
              }
            />
          </div>
          <div className="col-md">
            <UserPostList />
          </div>
          <div className="col-md">One of three columns</div>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps)(UserHome);
