import React, { Component } from "react";
import "../style/user-profile.css";
class UserProfile extends Component {
  render() {
    return (

      <div className="container-fluid">
        <div className="card">
          <img
            src="https://www.w3schools.com/w3images/team2.jpg"
            alt="John"
            style={{ width: "100%" }}
          />
          <a href="#kk"><h1  style={{"font-size":"2vw"}}>John Doe</h1></a>
          <p className="title" style={{"font-size":"1vw"}}>CEO & Founder, Example</p>
          <p style={{"font-size":"1vw"}} >Harvard University</p>
          <span><p style={{"font-size":"1vw"}}>Nombre de Tweets: <a href="/">{5}</a></p></span>
          <span><p style={{"font-size":"1vw"}}>Nombre d'amis': <a href="/">{5}</a></p></span>
        </div>
      </div>


    );
  }
}

export default UserProfile;


