import axios from "axios";
import { push } from "react-router-redux";

const URL = "http://localhost:8080/twister-server";

export const LOGIN = "LOGIN";
export const LOGOUT = "LOGOUT";
export const SIGNUP = "SIGNUP";

export function login(username, password) {
  return function (dispatch) {
    var params = new URLSearchParams();
    params.append("login", username);
    params.append("password", password);
    var request = {
      params: params,
    };


    axios
      .get(`${URL}/login`, request)
      .then((response) => {
        if (response.data.code === -1) {

          const getUsers = async () => {
            return await getFriendList(response.data.Key)
          }

          console.log(getUsers())
          dispatch(
            push({ pathname: "/account", state: { user: response.data, friendList: getFriendList(response.data.Key) } })
          );
        }
      })
      .catch((error) => {
        alert("error");
      });

  };
}

export function signup(name, lastname, email, password, sex, birthdate) {
  return function (dispatch) {
    var params = new URLSearchParams();
    params.append("nom", name);
    params.append("prenom", lastname);
    params.append("login", email);
    params.append("password", password);
    params.append("sex", sex);
    params.append("birth_date", birthdate);
    var request = {
      params: params,
    };
    axios
      .get(`${URL}/createUser`, request)
      .then((response) => {
        if (response.data.code === -1) {
          dispatch(push("/login"));
        }
      })
      .catch((error) => {
        alert("error");
      });
  };
}

export function logout(key) {
  return function (dispatch) {
    var params = new URLSearchParams();
    params.append("key", key);
    var request = {
      params: params,
    };
    axios
      .get(`${URL}/logout`, request)
      .then((response) => {
        dispatch({ type: LOGOUT, payload: response.data });
        if (response.data.code === -1) {
          dispatch(push({ pathname: "/login", state: {} }));
        }
      })
      .catch((error) => {
        alert("error");
      });
  };
}

function getFriendList(key) {
  var params = new URLSearchParams();
  params.append("key", key);
  var request = {
    params: params,
  };

  let res = axios.get(`${URL}/listerFriends`, request);
  return res.data;
}
