import { LOGIN } from "../actions/index";

const initialState = {};

export default function (state = initialState, action) {
    switch (action.type) {
      case LOGIN:
        return {
          userfriendlist: [...state.userfriendlist, ...action.payload.userfriendlist],
          userfriendsCommentsList: [...state.userfriendsCommentsList, ...action.payload.userfriendsCommentsList],
          userCommentsList: [...state.userCommentsList, ...action.payload.userCommentsList],

        };
      default:
        return state;
    }
  }
