import React, { Component } from "react";
class UserProfile extends Component {
  render() {
    return (
      <div className="userprofile card mb-3" style={{ "max-width": "540px" }}>
        <div class="card-header"><h3 style={{"font-size":"2vw"}}>John Doe</h3></div>
        <div className="userprofile row no-gutters">
          <div className="userprofile col-md-4">
            <img src="https://www.w3schools.com/w3images/team2.jpg" className="userprofile card-img" alt="profileImage" />
          </div>
          <div className="userprofile col-md-8">
            <div className="userprofile card-body">
              <h5 className="userprofile card-title">Card title</h5>
              <p className="userprofile card-text">
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </p>
              <p className="userprofile card-text">
                <small className="userprofile text-muted">Last updated 3 mins ago</small>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UserProfile;

