import React, { Component } from "react";
import PostRepliesList from '../containers/post-replies-list'
import "../style/user-post.css";

class Post extends Component {
  constructor(props) {
    super(props);
    this.state = { collapsereplies: true };
  }

  onClickComment(event) {
    event.preventDefault();
    this.setState({ collapsereplies: !this.state.collapsereplies });
  }

  renderReplies() {
    if (this.state.collapsereplies) {
      return (
        <PostRepliesList/>
      );
    }
  }
  render() {
    return (
      <div className="usepost container mt-8">
        <div className="usepost d-flex justify-content-center row">
          <div className="usepost col-md-12">
            <div className="usepost d-flex flex-column comment-section">
              <div className="usepost bg-white p-2">
                <div className="usepost d-flex flex-row user-info">
                  <img
                    className="usepost rounded-circle"
                    src="https://i.imgur.com/RpzrMR2.jpg"
                    width="40"
                    alt=""
                  />
                  <div className="usepost d-flex flex-column justify-content-start ml-2">
                    <span className="usepost d-block font-weight-bold name">
                      Marry Andrews
                    </span>
                    <span className="usepost date text-black-50">
                      Shared publicly - Jan 2020
                    </span>
                  </div>
                </div>
                <div className="usepost mt-2">
                  <p className="usepost comment-text">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua. Ut enim ad minim veniam, quis nostrud exercitation
                    ullamco laboris nisi ut aliquip ex ea commodo consequat.
                  </p>
                </div>
              </div>
              <div className="usepost bg-white">
                <div className="usepost d-flex flex-row fs-12">
                  <div className="usepost like p-2 cursor">
                    <i className="usepost fa fa-thumbs-o-up"></i>
                    <span className="usepost ml-1">Like</span>
                  </div>
                  <div className="usepost like p-2 cursor">
                    <i className="usepost fa fa-commenting-o"></i>
                    <span className="usepost ml-1" onClick={this.onClickComment.bind(this)}>Comment</span>
                  </div>
                  <div className="usepost like p-2 cursor">
                    <i className="usepost fa fa-reply"></i>
                    <span className="usepost ml-1">Reply</span>
                  </div>
                </div>
              </div>
              {
                this.renderReplies()
              }
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Post;
