import { LOGIN } from "../actions/index";
import { ADD_POST } from "../actions/index";

const initialState = {};

export default function (state = initialState, action) {
    switch (action.type) {
      case LOGIN:
        return {
          ...action.payload
        };
      case ADD_POST:
        state.userCommentsList = [...state.userCommentsList, action.payload]
        console.log(state)
        return state;
      default:
        return state;
    }
  }
