import React, { Component } from "react";
import { connect } from "react-redux";
import { addPost } from "../actions/index";



const mapDispatchToProps = {
  addPost,
};
function mapStateToProps(state) {
  return {
    user: state.routing.locationBeforeTransitions.state.user,
    userCommentsList: state.routing.locationBeforeTransitions.state.userfriendsCommentsList,
  };
}

class UserAddPost extends Component {
  constructor(props) {
    super (props);
    this.state = {text:""}
  }

  onChangeText(event){
    this.setState({text:event.target.value})
    event.preventDefault();
  }
  onClickAddPost() {
    const comment = { author: this.props.user.nom+" "+this.props.user.prenom, 
                      date: new Date(), text: this.state.text, replies: [] };

    this.props.addPost(this.props.Key, this.props.userCommentsList, comment)

  }

  add(key, userCommentsList, comment) {
    console.log("");
  }
  render() {
    console.log(this.props.user)
    return (
      <div className="bg-light p-2">
        <div className="d-flex flex-row align-items-start">
          <img
            className="rounded-circle"
            src="https://i.imgur.com/RpzrMR2.jpg"
            width="40"
            alt=""
          />
          <textarea className="form-control ml-1 shadow-none textarea" onChange={this.onChangeText.bind(this)} ></textarea>
        </div>
        <div className="mt-2 text-right">
          <button
            className="btn btn-primary btn-sm shadow-none"
            type="button"
            // onClick={this.onClickPostComment.bind(this)}
          >
            Post comment
          </button>
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps)(UserAddPost);
