import React, { Component } from 'react';
import '../style/style.css'
import { connect } from 'react-redux';
import { login } from "../actions/index";


const mapStateToProps = (store) => {
  return {
    user: store.userReducer.user,
  };
};

const mapDispatchToProps = {
  login,
};


class LoginForm extends Component {

  constructor(props) {
    super(props)
    this.state = {username:"", password:"", rememberme:false}
  }

  onSubmitLoginForm(e){
    console.log(e)
  }

  handleChangeUsername(e){
    this.setState({username:e.target.value})
  }

  handleChangePassword(e){
    this.setState({password:e.target.value})
  }

  handleChangeRememberMe(e){
    this.setState({rememberme: !this.state.rememberme})
    console.log(this.state.rememberme)
  }

  render() {
    return (
      <div className="container-fluid">
        <div>
          <div className="container-fluid">
            <div className="row main-content bg-success text-center">
              <div className="col-md-4 text-center company__info">
                <div className="company__logo">
                  <img
                    className="rounded mx-auto d-block logo"
                    src="https://logos.mybrandnewlogo.com/logos/Az2kxWD0XwI4W8pnBDvk/images/logo-Pwkxq5mKp1izl52XkYL9SD2p2x40.png"
                    alt="logo"
                    width="300"
                    height="400"
                  />
                </div>
              </div>
              <div className="col-md-8 col-xs-12 col-sm-12 login_form " onSubmit={e => this.onSubmitLoginForm(e)}>
                <div className="container-fluid">
                  <div className="row">
                    <h2>Log In</h2>
                  </div>
                  <div className="row">
                    <form control="" className="form-group" method="GET" onSubmit={e => this.onSubmitLoginForm(e)}>
                      <div className="row">
                        <input
                          required
                          type="text"
                          name="username"
                          id="username"
                          className="form__input"
                          placeholder="Username"
                          onChange={e => this.handleChangeUsername(e)}
                        />
                      </div>
                      <div className="row">
                        <input
                          required
                          onChange={e => this.handleChangePassword(e)}
                          type="password"
                          name="password"
                          id="password"
                          className="form__input"
                          placeholder="Password"
                        />
                      </div>
                      <div className="row">
                        <input
                          onChange={e => this.handleChangeRememberMe(e)}
                          type="checkbox"
                          name="remember_me"
                          id="remember_me"
                          className=""
                        />
                        <label className="remember_me" htmlFor="remember_me">
                          Remember Me!
                        </label>
                      </div>
                      <div className="row">
                        <input type="submit" value="Submit" className="btn" />
                      </div>
                    </form>
                  </div>
                  <div className="row have_account">
                    <p>
                      Don't have an account? <a href="#">Register Here</a>
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}


export default connect(
  mapStateToProps, mapDispatchToProps
)(LoginForm);
